mapper.update:
		UPDATE
			TB_CODE
		SET
			CODE_ID = #{code_id}
			, CODE_NAME = #{code_name}
			, CODE_LEVEL = #{code_level}
			, UPPER_CODE = #{upper_code}
			, CODE_DC = #{code_dc}
			, UPD_DATETIME = DATE_FORMAT(NOW(),'%Y%m%d')
			, UPD_ID = #{upd_id}
			, NOTE = #{note}
			, TEMP_COLUM = #{temp_colum}
		WHERE
			CODE_ID = #{code_id}
s
