UPDATE TB_EXPENSE
EXPENS_TY_CODE=#{EXPENS_TY_CODE}, USE_DATE=#{USE_DATE}, USE_AMOUNT=#{USE_AMOUNT}, USE_CN=#{USE_CN}, EXPENS_ATCHMNF_ID=#{EXPENS_ATCHMNF_ID}, UPD_ID=#{MEMBER_NO}, UPD_DATETIME=NOW()
