mapper.select:
SELECT @rownum := @rownum-1 AS rnum , A.EXPENS_NO seq , C.CODE_NAME expenseTypeText , A.USE_CN memo , A.SANCTN_STTUS_CODE status , D.CODE_NAME statusText , B.NAME register , A.USE_AMOUNT pay , A.USE_DATE payDate , A.RETURN_CN rejectMemo , E.NAME prevAuthPerson , F.NAME authPerson , A.FIR_SANCTN_DATETIME prevAuthDate , A.SCD_SANCTN_DATETIME authDate FROM TB_EXPENSE A LEFT JOIN TB_MEMBER B ON A.MEMBER_NO = B.MEMBER_NO LEFT JOIN TB_CODE C ON A.EXPENS_TY_CODE = C.CODE_ID LEFT JOIN TB_CODE D ON A.SANCTN_STTUS_CODE = D.CODE_ID LEFT JOIN TB_MEMBER E ON A.FIR_SANCTENER_MEMBER_NO = E.MEMBER_NO LEFT JOIN TB_MEMBER F ON A.SANCTNER_MEMBER_NO = F.MEMBER_NO JOIN (SELECT @rownum := #{listCount}+1) R
AND A.MEMBER_NO = #{MEMBER_NO}
AND B.NAME LIKE CONCAT('%',#{name},'%')
AND A.EXPENS_TY_CODE = #{expenseType}
AND A.SANCTN_STTUS_CODE = #{status}
AND A.USE_CN LIKE CONCAT('%',#{memo},'%')
AND DATE_FORMAT(A.USE_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{payStDt}, '%Y-%m-%d') AND DATE_FORMAT(#{payEdDt}, '%Y-%m-%d')
ORDER BY A.SANCTN_STTUS_CODE ASC, A.EXPENS_NO DESC

SELECT COUNT(*)
FROM TB_EXPENSE A
LEFT JOIN TB_MEMBER B
ON A.MEMBER_NO = B.MEMBER_NO

	
		AND A.MEMBER_NO = #{MEMBER_NO}
	
	
		AND B.NAME LIKE CONCAT('%',#{name},'%')
	
	
		AND A.EXPENS_TY_CODE = #{expenseType}
	
	
		AND A.SANCTN_STTUS_CODE = #{status}
	
	
		AND A.USE_CN LIKE CONCAT('%',#{memo},'%')
	
	AND DATE_FORMAT(A.USE_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{payStDt}, '%Y-%m-%d') AND DATE_FORMAT(#{payEdDt}, '%Y-%m-%d')

SELECT A.EXPENS_NO seq , B.NAME name , B.PHONE_NUM phone , B.EMAIL email , A.EXPENS_TY_CODE expenseType , C.CODE_NAME expenseTypeText , A.USE_CN memo , A.SANCTN_STTUS_CODE status , D.CODE_NAME statusText , B.NAME register , A.USE_AMOUNT pay , A.USE_DATE payDate , A.RETURN_CN rejectMemo , A.FIR_SANCTENER_MEMBER_NO prevAuthPersonNO , A.SANCTNER_MEMBER_NO authPersonNO , E.NAME prevAuthPerson , F.NAME authPerson , A.FIR_SANCTN_DATETIME prevAuthDate , A.SCD_SANCTN_DATETIME authDate , A.EXPENS_ATCHMNF_ID filePath FROM TB_EXPENSE A LEFT JOIN TB_MEMBER B ON A.MEMBER_NO = B.MEMBER_NO LEFT JOIN TB_CODE C ON A.EXPENS_TY_CODE = C.CODE_ID LEFT JOIN TB_CODE D ON A.SANCTN_STTUS_CODE = D.CODE_ID LEFT JOIN TB_MEMBER E ON A.FIR_SANCTENER_MEMBER_NO = E.MEMBER_NO LEFT JOIN TB_MEMBER F ON A.SANCTNER_MEMBER_NO = F.MEMBER_NO
AND A.EXPENS_NO = #{EXPENS_NO}
AND ( A.FIR_SANCTENER_MEMBER_NO=#{MEMBER_NO} OR A.SANCTNER_MEMBER_NO=#{MEMBER_NO} )

