mapper.select:
SELECT A.MEMBER_NO mno , DATE_FORMAT(A.INPT_BGNDE, '%Y-%m-%d') startDate , DATE_FORMAT(A.INPT_ENDDE, '%Y-%m-%d') endDate , B.TRANSPORT_CT ct FROM TB_PROJECT_MEMBER A LEFT JOIN TB_PROJECT B ON A.PROJECT_NO = B.PROJECT_NO
AND A.MEMBER_NO = #{MEMBER_NO}
AND A.MEMBER_NO != '99999999' AND (DATE_FORMAT(A.INPT_BGNDE, '%Y') = #{YEAR} OR DATE_FORMAT(A.INPT_ENDDE, '%Y') = #{YEAR})
