mapper.update:
UPDATE
	TB_MEM_HIST
SET
	PROJECT_NM = #{PROJECT_NM}
	, PROJECT_NO = #{PROJECT_NO}
	, INSTT_CODE = #{INSTT_CODE}
	, INSTT_NM = #{INSTT_NM}
	, INPT_BGNDE = #{INPT_BGNDE}
	, INPT_ENDDE = #{INPT_ENDDE}
	, ROLE_CODE = #{ROLE_CODE}
	, CHRG_JOB = #{CHRG_JOB}
	, USE_LANG = #{USE_LANG}
	, UPD_DATETIME = DATE_FORMAT(NOW(),'%Y%m%d')
	, UPD_ID = #{UPD_ID}
WHERE
	MEM_HIST_NO = #{MEM_HIST_NO}

mapper.select:
SELECT HIST.MEM_HIST_NO , HIST.MEMBER_NO , HIST.PROJECT_NO , HIST.PROJECT_NM , HIST.INSTT_CODE , HIST.INSTT_NM , HIST.INPT_BGNDE , HIST.INPT_ENDDE , CONCAT(date_format(HIST.INPT_BGNDE,'%Y-%m-%d'), "~" , date_format(HIST.INPT_ENDDE,'%Y-%m-%d')) AS TERM , HIST.ROLE_CODE , HIST.CHRG_JOB , HIST.USE_LANG , HIST.REG_DATETIME , HIST.UPD_DATETIME , HIST.REG_ID , HIST.UPD_ID , HIST.NOTE , HIST.TEMP_COLUM , MEM.NAME AS MEMBER_NAME FROM TB_MEM_HIST AS HIST LEFT JOIN TB_MEMBER AS MEM ON MEM.MEMBER_NO = HIST.MEMBER_NO WHERE 1 = 1
AND HIST.MEMBER_NO like #{MEMBER_NO}
ORDER BY HIST.MEMBER_NO DESC, INPT_BGNDE DESC
