mapper.select:
SELECT TB_RESOURCE.RES_NO, TB_RESOURCE.MODEL_NM, DATE_FORMAT(STR_TO_DATE(TB_RESOURCE.PRODUCT_MTN, '%Y%m'),'%Y-%m ') AS PRODUCT_MTN, DATE_FORMAT(STR_TO_DATE(TB_RESOURCE.PURCHASE_MTN, '%Y%m'),'%Y-%m ') AS PURCHASE_MTN, TB_RESOURCE.SERIAL_NO, TB_RESOURCE.MAC_ADDR, TB_RESOURCE.REG_ID, A.CODE_NAME AS RES_CODE, B.CODE_NAME AS MARK_CODE, C.CODE_NAME AS DISPLAY_SIZE_CODE, D.NAME AS HOLDER FROM TB_RESOURCE LEFT OUTER JOIN TB_CODE A ON TB_RESOURCE.RES_CODE = A.CODE_ID LEFT OUTER JOIN TB_CODE B ON TB_RESOURCE.MARK_CODE = B.CODE_ID LEFT OUTER JOIN TB_CODE C ON TB_RESOURCE.DISPLAY_SIZE_CODE = C.CODE_ID LEFT OUTER JOIN TB_MEMBER D ON TB_RESOURCE.HOLDER = D.MEMBER_NO
AND D.NAME LIKE CONCAT('%',#{holder},'%')
AND TB_RESOURCE.RES_CODE = #{resType}
AND CAST(#{stDt} as unsigned) <= CAST(TB_RESOURCE.PURCHASE_MTN as unsigned)
AND CAST(#{edDt} as unsigned) >= CAST(TB_RESOURCE.PURCHASE_MTN as unsigned)
ORDER BY TB_RESOURCE.RES_NO DESC LIMIT #{offset}, #{limit};

SELECT MEMBER_NO AS id, NAME AS label FROM TB_MEMBER
TB_MEMBER.MEMBER_NO != "99999999"
AND TB_MEMBER.MEMBER_NO = #{memberNo}
